INSERT [ZL_Mis_Model] ([ID],[ModelName],[ModelContent],[CreateTime],[DocType]) VALUES ( 2,N'会议邀请函',N'<p style="margin-top:auto;margin-bottom: auto;text-align:center"><span style="font-size:35px;font-family:黑体">邀请函</span></p><p><span style="font-size:14px;font-family:宋体">尊敬的<span style="text-decoration:underline;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&nbsp;先生/女士：</span></p><p style="margin-top:auto;margin-bottom:auto;text-indent:28px"><span style="font-size:14px;font-family:宋体">您好！ </span></p><p style="margin-top:auto;margin-bottom:auto;text-indent:28px"><span style="font-size:14px;font-family:宋体">我们很荣幸地邀请您参加将于</span><span style="font-size:14px;font-family:宋体"><a name="Text1"></a></span><span style="font-size:14px;font-family:宋体">【会议日期】</span><span style="font-size:14px;font-family:宋体">在</span><span style="font-size:14px;font-family:宋体"><a name="Text2"></a></span><span style="font-size:14px;font-family:宋体">【会议地点】</span><span style="font-size:14px;font-family:宋体">举办的</span><span style="font-size:14px;font-family:宋体"><a name="Text3"></a></span><span style="font-size:14px;font-family:宋体">【会议名称】</span><span style="font-size:14px;font-family:宋体">。本次会议的主题是：</span><span style="font-size:14px;font-family:宋体"><a name="Text4"></a></span><span style="font-size:14px;font-family:宋体">【会议主题】</span><span style="font-size:14px;font-family:宋体">。此次</span><span style="font-size:14px;font-family:宋体"><a name="Text5"></a></span><span style="font-size:14px;font-family:宋体">【会议主办方】</span><span style="font-size:14px;font-family:宋体">主办，</span><span style="font-size:14px;font-family:宋体"><a name="Text6"></a></span><span style="font-size:14px;font-family:宋体">【会议协办方】</span><span style="font-size:14px;font-family:宋体">。届时，来自</span><span style="font-size:14px;font-family:宋体"><a name="Text7"></a></span><span style="font-size:14px;font-family:宋体">【会议参加人员】</span><span style="font-size:14px;font-family:宋体">将参加会议。&nbsp; </span></p><p><span style="font-size:14px;font-family:宋体