CREATE  proc [dbo].[PR_ModelField_Single_NewTable]
@NodeID int,
@ModelName NVarChar(50),
@TableName nvarchar(50),
@FieldNames nvarchar(1000) ,--接收以分号分隔的字段名称
@FieldAliass nvarchar(1000)--接收以分号分隔的字段描述 此功能暂未开发
--@FieldTypes nvarchar(50)--统一字段类型nvarchar 255 TextType
AS
select * from ZL_Model
	declare @ModelID int
	INSERT INTO 
	[ZL_Model] 
	(  
		[ModelName],  
		[TableName],
		ItemName,
	 	ModelType,
	    MultiFlag,
		ItemIcon
		)
		VALUES
		(
		@ModelName,	
		@TableName,
		@ModelName,
		1,1,'Default.gif'
		)
	
	--获取模型id
	select @ModelID = @@identity

	--更新此模型与Node的关联
	update zl_Node set contentmodel=
	case contentmodel when '' then cast(@ModelID as varchar(10))
		else contentmodel+','+cast(@ModelID as varchar(10)) end
	where nodeid=@NodeID
	
	declare @fieldname   varchar(50),   
		@s_point   int,   
		@t_point   int,   
		--@t   int,
		@fieldnamesublen   int,
		@pos   int   
    
	--临时变量   
	select @s_point=0,@t_point=0,@pos=0
	--获取第一个值
	select   @t_point=charindex(';',@fieldNames,@s_point) 

	--存储创建表sql语句的变量
	declare @creatTable nvarchar(1000)
	set @creatTable = N'CREATE TABLE dbo.'+ @TableName+' ([ID] [int] IDENTITY (1, 1) PRIMARY Key'

	while(1=1)
	begin
		if @t_point>0
		begin
			set   @fieldnamesublen=@t_point-@s_point
			set   @fieldname=rtrim(ltrim(substring(@fieldNames,@s_point,@fieldnamesublen)))
			--insert   filed(modelid,fieldname,FieldAlias,FieldType)  values(@modelId,@fieldname,@fieldname,'textType')
		end
		else
		begin
			set   @fieldnamesublen=datalength(@fieldNames)-@t_point+1
			set   @fieldname=rtrim(ltrim(substring(@fieldNames,@s_point,@fieldnamesublen)))
			--insert   filed(modelid,fieldname,FieldAlias,FieldType)  values(@modelId,@fieldname,@fieldname,'textType')
		
		end
		insert into  ZL_ModelField(modelid,fieldname,FieldAlias,FieldType,content)  values(@modelId,@fieldname,@fieldname,'TextType','TitleSize=35,IsPassword=text,DefaultValue=')
		set @creatTable = @creatTable + N',' +  @fieldname + N' nvarchar(255)' 	
		if   @t_point<=0	
	 		goto tend
	-- 	
	 	select   @s_point=@t_point+1
	 	select   @t_point=charindex(';',@fieldNames,@s_point)
		
	end 
	    tend:
	 	set @creatTable = @creatTable + N')'
		execute sp_executesql @creatTable
	return @ModelID

GO
