@pagecount int output--总页数，共有多少页

AS
declare @countsql nvarchar(4000)
declare @sql nvarchar(4000)
declare @subsql nvarchar(4000)--not in子sql语句
declare @tmpOrderid nvarchar(50)
--返回总记录数，并赋值给输出参数@rowcount
