Create  proc [dbo].[pr_GetNodeIdOption] (@nodeid varchar(100),@generalid varchar(100))
as
BEGIN

CREATE TABLE #temp1(nodeid int)
while(charindex(',',@nodeid)>1)
begin
INSERT INTO #temp1 VALUES(cast(substring(@nodeid,0,charindex(',',@nodeid)) AS int))
SET @nodeid = substring(@nodeid,charindex(',',@nodeid)+1,len(@nodeid))
end 
IF len(@nodeid)>1
INSERT INTO #temp1 VALUES(cast(@nodeid AS int))

CREATE TABLE #temp2(generalid int)	
while(charindex(',',@generalid)>1)
begin
INSERT INTO #temp2 VALUES(cast(substring(@generalid,0,charindex(',',@generalid)) AS int))
SET @generalid = substring(@generalid,charindex(',',@generalid)+1,len(@generalid))
end 
IF len(@generalid)>1
INSERT INTO #temp2 VALUES(cast(@generalid AS int))


if(select count(*) from zl_node a,ZL_Model b 
where a.nodeid IN (SELECT NodeID FROM #temp1) AND b.ModelID LIKE '%'+a.contentmodel+'%'
and b.frommodel NOT in
(select b.frommodel from zl_commonmodel a,zl_model b 
where a.generalid IN (SELECT GeneralID FROM #temp2) AND a.modelid=b.modelid)
)>0
begin
select a.* from zl_node a,ZL_Model b 
where a.nodeid IN (SELECT NodeID FROM #temp1) AND b.ModelID LIKE '%'+a.contentmodel+'%'
and b.frommodel NOT in
(select b.frommodel from zl_commonmodel a,zl_model b 
where a.generalid IN (SELECT GeneralID FROM #temp2) AND a.modelid=b.modelid)
return
end


if(select count(*) from zl_node a,ZL_Model b 
where a.nodeid IN (SELECT NodeID FROM #temp1) AND b.ModelID LIKE '%'+a.contentmodel+'%'
and b.frommodel in
(select b.frommodel from zl_commonmodel a,zl_model b 
where a.generalid IN (SELECT GeneralID FROM #temp2) AND a.modelid=b.modelid)
)=(SELECT count(*)FROM #temp1)

begin
Declare @nid int,@gid int,@modelid int,@column varchar(50),@co varchar(500)
declare @sql varchar(500),@itemid varchar(200),@tbname varchar(50),@tbname1 varchar(50),@tb varchar(50)

BEGIN TRANSACTION

Declare Cur Cursor for select a.NodeID,b.ModelID,b.tablename from zl_node a,ZL_Model b 
where a.nodeid IN (SELECT NodeID FROM #temp1) AND b.ModelID LIKE '%'+a.contentmodel+'%'
and b.frommodel in
(select b.frommodel from zl_commonmodel a,zl_model b 
where a.generalid IN (SELECT GeneralID FROM #temp2) AND a.modelid=b.modelid)

Open Cur
Fetch next From Cur Into @nid,@modelid,@tb
While @@fetch_status=0     
begin

Declare Cur1 Cursor for select generalid from zl_commonmodel where  generalid IN(SELECT GeneralID FROM #temp2)
Open Cur1
Fetch next From Cur1 Into @gid
While @@fetch_status=0     
begin

insert into zl_commonmodel(nodeid,modelid,itemid,tablename,title,inputer,hits,CreateTime,status,elitelevel,
infoid,specialid,iscreate,htmllink,template,tagkey,UpDateTime,titlecolor,pdflink,cast_file)
 select @nid,@modelid,itemid,@tb,title,inputer,hits,getdate(),0,elitelevel,
infoid,specialid,iscreate,htmllink,template,tagkey,getdate(),titlecolor,pdflink,@gid from zl_commonmodel where generalid=@gid


set @tbname=(select tablename from zl_commonmodel where generalid=@gid)
set @itemid=(select itemid from zl_commonmodel where generalid=@gid)
set @tbname1=(select tablename from zl_commonmodel where generalid=(select max(generalid) from zl_commonmodel))--新内容

--将表中的所有字段获取
if(select count(*) from zl_model where modelid=(select frommodel from zl_model 
	where modeLid=(select modeLid from zl_commonmodel where generalid=@gid)))>0
begin
set @tbname=(select tablename from zl_model where modelid=(select frommodel from zl_model 
				where modeLid=(select modeLid from zl_commonmodel where generalid=@gid)))
end

set @co=''
Declare Cur4 Cursor for select column_name from information_schema.columns where table_name=
 @tbname  and column_name<>'id' order by column_name
 open cur4
fetch next from cur4 into @column
while @@fetch_status=0
begin 
	set @co=@co+@column+','
fetch next from cur4 into @column
end 
close cur4 
deallocate cur4 

set @tbname=(select tablename from zl_commonmodel where generalid=@gid)
--插入一条数据
set @co=substring(@co,1,len(@co)-1)
set @sql=' insert into '+@tbname1+'('+@co+') select '+@co+' from '+@tbname+' where id='+@itemid

exec(@sql)

--更新zl_commonmodel 中新插入的 itemid
 set @sql='update zl_commonmodel set itemid=(select max(id) from '+@tbname1+') where generalid=(select max(generalid) from zl_commonmodel)'
exec(@sql)

 Fetch Next From Cur1 Into @gid
end
Close Cur1   
Deallocate Cur1

 Fetch Next From Cur Into @nid,@modelid,@tb
end
Close Cur   
Deallocate Cur

IF @@error <> 0
BEGIN
     ROLLBACK TRANSACTION
select top 1 ' ' as NodeName from zl_node
return

end
else
begin
COMMIT TRANSACTION
select top 1 '1' as NodeName from zl_node
return 
end
end
end


